// INPUT
// Home Battery Start payload

// Process
let solution_array = [];

// Add a solution per battery
msg.batteries.forEach(battery => {
    solution_array.push(
        { 
            id: battery.id,     // Battery label
            mode: "stop",       // "stop", "charge", "discharge"
            power: 0            // Watt
        }); 
});

// OUTPUT
return { payload: solution_array };

// Add return node information
let return_to = flow.get("_linkSource");
msg._linkSource = return_to;

// return to calling flow
return msg;
